import React from 'react';
import './Rules.css';

const Rules = () => {
  const tenses = [
    { title: "Present Simple", description: "I do / He does. Используется для регулярных действий." },
    { title: "Present Continuous", description: "I am doing. Используется для действий, происходящих сейчас." },
    { title: "Present Perfect", description: "I have done. Используется для результата действия." },
    { title: "Past Simple", description: "I did. Используется для действий в прошлом." },
    { title: "Past Continuous", description: "I was doing. Для длительных действий в прошлом." },
    { title: "Past Perfect", description: "I had done. Для действий до определённого момента в прошлом." },
    { title: "Future Simple", description: "I will do. Используется для будущих действий." },
    { title: "Future Continuous", description: "I will be doing. Действия, которые будут длиться в будущем." },
    { title: "Future Perfect", description: "I will have done. Действия, завершённые до момента в будущем." },
  ];

  return (
    <div className="rules-container">
    <h1>Grammar Rules</h1>
    <div className="cards-wrapper">
      {tenses.map((tense, index) => (
        <div key={index} className="card">
          <h2>{tense.title}</h2>
          <p>{tense.description}</p>
        </div>
      ))}
    </div>
  </div>
  );
};

export default Rules;
